/**
 * jQuery LazyLoad
 * A very lightweight jQuery plugin to lazy load trigger 
 *example
  $(document).ready(function() {
     $("img").lazyload(function(){
     };
  });
 */

;(function($) {

  $.fn.lazyload = function(options) {
	  var settings = { threshold: 0,load:null};
      $.extend(settings, options);
      var $w = $(window),
        th = settings.threshold || 0,
        elements = this,
        loaded,
        container=settings.container,
        inview;
      
        
    this.one("lazyload", function() {
      if(typeof settings.load=="function")
    	  settings.load(this);
    });

    function lazyload() {
      inview = elements.filter(function() {
        var $e = $(this),
            wt = $w.scrollTop(),
            wb = wt + $w.height(),
            et = $e.offset().top,
            eb = et + $e.height();

        return eb >= wt - th && et <= wb + th;
      });
      loaded=inview.trigger("lazyload");
      elements = elements.not(loaded);
    }

    $w.scroll(lazyload);
    $w.resize(lazyload);
    this.click(lazyload);
    if(container)
    	$(container).scroll(lazyload);
    lazyload();
    return this;
  };

})(jQuery);



